import React, { useState } from 'react';
import '../../styles/aboutUsStyle/aboutUsCardStyle.css';

const AboutUsCard = ({ title, text }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpen = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div class="info">
      <input onClick={toggleOpen} id="info__body_1" class="info__switch" type="checkbox" />
      <label for="info__body_1" class="info__headline">
        {title}
      </label>
      {isOpen && <div class="info__body">{text}</div>}
    </div>
  );
};

export default AboutUsCard;
